var mapOpen = document.querySelector(".contacts-block__map"),
  mapPopup = document.querySelector(".modal-map"),
  mapClose = mapPopup.querySelector(".modal-map .modal-close-btn");
mapOpen.addEventListener("click", function(a) {
  a.preventDefault(), overlay.classList.add("modal-overlay--show"), mapPopup.classList.add("modal-show")
}), mapClose.addEventListener("click", function(a) {
  a.preventDefault(), overlay.classList.remove("modal-overlay--show"), mapPopup.classList.remove("modal-show")
}), overlay.addEventListener("click", function(a) {
  mapPopup.classList.remove("modal-show"), overlay.classList.remove("modal-overlay--show")
}), window.addEventListener("keydown", function(a) {
  27 === a.keyCode && mapPopup.classList.contains("modal-show") && (overlay.classList.remove("modal-overlay--show"), mapPopup.classList.remove("modal-show"))
});
